import React, { Component } from "react";
import { KaraokeClient } from '../client/KaraokeClient';
import  Music  from './music.component'

export default class Player extends Component {
    KaraokeClient = new KaraokeClient();

    constructor(){
        super();
        this.state = {
          data: 'a',
          urla: 'https://songs-spectacular-karaoke.s3.us-east-2.amazonaws.com/Tupac_Changes.mp3'

        }
      }
    
      componentDidMount(){
        this.loadData();
      }

      async loadData(){
        const newData = await this.KaraokeClient.getSongsData();
        this.setState({
          data:newData
        });
      }

    clickIcon(){
        console.log('HOLA')
       
    }

    render() {
        return (
            <div class="card mb-4">

              <div class="card mb-4">
                            <div class="card-header">
                                <i class="fas fa-table me-1"></i>
                                Featured Song List
                            </div>
                            

                            <div class="list-group list-group-flush">
                            {Array.from(this.state.data).map((value) =>
                            <button type="button" class="list-group-item list-group-item-action d-flex justify-content-between align-items-start">
                            {value.Key}
                            <span class="badge bg-primary rounded-pill">14</span>
                            </button>)}
                            </div>
                        </div>
                            <div class="card-header">
                            <i class="fas fa-music me-1"> </i>
                                Current song <span type="button" class="badge bg-primary rounded-pill">Sync</span>
                                <div class="progress">
                                <div class="progress-bar" role="progressbar" style={{width: 400}} aria-valuenow="00" aria-valuemin="0" aria-valuemax="100"></div>
                                </div>
                            </div>

                            <audio id="mySong">
                                <source src="binks.mp3" type="audio/mp3"/>
                            </audio>

                            
                            
                            <Music url={this.state.urla}></Music>
                        </div>
        
)
}
}
